import React, { useCallback, useRef } from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios'

const Img = ({video}) => {
  const [img, setImg] = useState("")
  const [key, setKey] = useState("")
  console.log(video)
  useEffect(()=> {
    axios.get(`http://localhost:8000/api/video/${video}`)
      .then((res) => {
        const dataTemp = res.data
        setImg(dataTemp[0])
        setKey(Math.random())
      })

      let timer = setInterval(()=>{
        axios.get(`http://localhost:8000/api/video/${video}`)
        .then((res) => {
          const dataTemp = res.data
          setImg(dataTemp)
          setKey(Math.random())
        })
      },60 * 1000)

    return () => clearInterval(timer);
  }, [])

  return(
    <>
      <img key={key} src={`data:image/png;base64,${img}`} alt="cctvImage" />
      {/* <img key={key} src={img} alt="cctvImage"></img> */}
    </>
  )
}

export default Img;
